ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    from System import DateTime

    row.GetCellByName("TOTALCUMULATIVOARECOLHER").BackgroundColor = "#B0C4DE";
    row.GetCellByName("TOTALCUMULATIVOARECOLHER").CssClass = "font-weight: bold";
    row.GetCellByName("TOTALCUMULATIVOARECOLHER").Align = "right";

    if entity == None: return

    #Zerando valores CUMULATIVO
    row.GetCellByName("VALORCONTRIBUICAOCUMULATIVA").Text = "0,00";
    row.GetCellByName("TOTALRETIDOFONTECUMULATIVO").Text = "0,00";
    row.GetCellByName("TOTALDEDUCOESCUMULATIVO").Text = "0,00";
    row.GetCellByName("TOTALCUMULATIVOARECOLHER").Text = "0,00";

    #Formatação dos valores CUMULATIVO
    if entity.Fields["VALORCONTRIBUICAOCUMULATIVA"] != None: row.GetCellByName("VALORCONTRIBUICAOCUMULATIVA").Text = str(str(str("{:,.2f}".format(entity.Fields["VALORCONTRIBUICAOCUMULATIVA"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
    if entity.Fields["TOTALRETIDOFONTECUMULATIVO"] != None: row.GetCellByName("TOTALRETIDOFONTECUMULATIVO").Text = str(str(str("{:,.2f}".format(entity.Fields["TOTALRETIDOFONTECUMULATIVO"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
    if entity.Fields["TOTALDEDUCOESCUMULATIVO"] != None: row.GetCellByName("TOTALDEDUCOESCUMULATIVO").Text = str(str(str("{:,.2f}".format(entity.Fields["TOTALDEDUCOESCUMULATIVO"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
    if entity.Fields["TOTALCUMULATIVOARECOLHER"] != None: row.GetCellByName("TOTALCUMULATIVOARECOLHER").Text = str(str(str("{:,.2f}".format(entity.Fields["TOTALCUMULATIVOARECOLHER"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");

    dataNula = DateTime(1899,12,30);
    if entity.Fields["VENCIMENTOCUMULATIVO"] == None or entity["VENCIMENTOCUMULATIVO"].Value == dataNula:
        row.GetCellByName("VENCIMENTOCUMULATIVO").Text = "";
