ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    if entity == None: return

    #Zerando valores
    row.GetCellByName("BASECALCULOCONTRIBUICAO").Text = "0,00";
    row.GetCellByName("VALOREXCLUSAOGERAL").Text = "0,00";
    row.GetCellByName("VALOREXCLUSAOBASECALCULO").Text = "0,00";
    row.GetCellByName("BASECALCULOAPOSEXCLUSOES").Text = "0,00";

    #Formatação dos valores
    if entity.Fields["BASECALCULOCONTRIBUICAO"] != None: row.GetCellByName("BASECALCULOCONTRIBUICAO").Text = str(str(str("{:,.2f}".format(entity.Fields["BASECALCULOCONTRIBUICAO"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
    if entity.Fields["VALOREXCLUSAOGERAL"] != None: row.GetCellByName("VALOREXCLUSAOGERAL").Text = str(str(str("{:,.2f}".format(entity.Fields["VALOREXCLUSAOGERAL"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
    if entity.Fields["VALOREXCLUSAOBASECALCULO"] != None: row.GetCellByName("VALOREXCLUSAOBASECALCULO").Text = str(str(str("{:,.2f}".format(entity.Fields["VALOREXCLUSAOBASECALCULO"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
    if entity.Fields["BASECALCULOAPOSEXCLUSOES"] != None: row.GetCellByName("BASECALCULOAPOSEXCLUSOES").Text = str(str(str("{:,.2f}".format(entity.Fields["BASECALCULOAPOSEXCLUSOES"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
