def InitializeRow(entity, row):
    if entity == None:
        return
    #if entity.Fields["BASECALCULOCONTRIBRECEITABRUTA"] >= 0:
        #row.GetCustomCommand("CMD_COMPENSAR").Enabled =  False

    RemovePrefixoMoedas("RECEITABRUTATOTAL", entity, row)
    RemovePrefixoMoedas("VALORRECEITABRUTAATIVIDADE", entity, row)
    RemovePrefixoMoedas("VALOREXCLUSAORECEITABRUTA", entity, row)
    RemovePrefixoMoedas("BASECALCULOCONTRIBRECEITABRUTA", entity, row)
    RemovePrefixoMoedas("VALORCONTRIBCALCAPURACAO", entity, row)

def RemovePrefixoMoedas(nomeCampo, entidade, linha):
        if entidade.Fields[nomeCampo] != None: linha.GetCellByName(nomeCampo).Text = str(str(str("{:,.2f}".format(entidade.Fields[nomeCampo])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
