def InitializeRow(entity, row):
    RemovePrefixoMoedas("PERCENTUALCONTRRECEITABRUTA", entity, row)
    RemovePrefixoMoedas("PERCENTUALCONTRREMUNERACAO", entity, row)
    RemovePrefixoMoedas("RECEITAPESSOAJURIDICA", entity, row)
    RemovePrefixoMoedas("RECEITACONTRRECEITABRUTA", entity, row)
    RemovePrefixoMoedas("RECEITACONTRREMUNERACAO", entity, row)

def RemovePrefixoMoedas(nomeCampo, entidade, linha):
        if entidade.Fields[nomeCampo] != None: linha.GetCellByName(nomeCampo).Text = str(str(str("{:,.2f}".format(entidade.Fields[nomeCampo])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
