def Load(widget):
entidade = widget.Entity if entidade == None: return
widget.View.Commands["Edit"].Visible = (entidade["DOCUMENTOFINANCEIRO"].IsNull and entidade["LANCAMENTOTESOURARIA"].IsNull) widget.View.Commands["Delete"].Visible = (entidade["DOCUMENTOFINANCEIRO"].IsNull and entidade["LANCAMENTOTESOURARIA"].IsNull)
RemovePrefixoMoedas("VALOROPERACAO", entidade) RemovePrefixoMoedas("BASECALCULOPIS", entidade) RemovePrefixoMoedas("VALORPIS", entidade) RemovePrefixoMoedas("BASECALCULOCOFINS", entidade) RemovePrefixoMoedas("VALORCOFINS", entidade)
def RemovePrefixoMoedas(nomeCampo, entidade): if entidade == None: return entidade.Fields[nomeCampo] = str(str(str("{:,.2f}".format(entidade[nomeCampo].GetDecimal())).replace(".","_")).replace(",",".")).replace("_",",").replace("-","")
