ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    from Benner.Tecnologia.Common import CurrencyFieldDefinition


    row.GetCellByName("HISTORICO").CssClass = "truncate"
    #row.GetCellByIndex(0).CssClass = "action-extrato"

    natureza = entity["NATUREZA"].Value.Value;
    valorCell = row.GetCellByName("VALOR");
    currency = CurrencyFieldDefinition();

    if natureza == "C":
        valorCell.TextColor = "#4B77BE"
        valorCell.Text = currency.ToUI(entity["VALOR"].GetDecimal())

    elif natureza == "D":
        valorCell.TextColor = "#E43A45"
        valorCell.Text = currency.ToUI(entity["VALOR"].GetDecimal() * -1)
