ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    from Benner.Tecnologia.Common import CurrencyFieldDefinition

    natureza = entity["NATUREZALANCAMENTO"].Value.Value;
    valorCell = row.GetCellByName("VALORLANCAMENTO");
    currency = CurrencyFieldDefinition();

    if entity["NATUREZALANCAMENTO"].GetDBValue() != None:
        cor = "text-primary"
        if entity["NATUREZALANCAMENTO"].GetDBValue() == "D":
            cor = "text-danger"

        row.GetCellByName("NATUREZALANCAMENTO").Text = "<div class='" + cor + "' style='width: 21px'>" + entity["NATUREZALANCAMENTO"].GetDBValue() + "</div>"
        row.GetCellByName("NATUREZALANCAMENTO").Tooltip = entity["NATUREZALANCAMENTO"].GetString()
        row.GetCellByName("NATUREZALANCAMENTO").Align = "center"

    if natureza == "C":
        valorCell.TextColor = "#4B77BE"
        valorCell.Text = currency.ToUI(entity["VALORLANCAMENTO"].GetDecimal())

    elif natureza == "D":
        valorCell.TextColor = "#E43A45"
        valorCell.Text = currency.ToUI(entity["VALORLANCAMENTO"].GetDecimal() * -1)
