def OnCONFIGAVALIACAOValueChanged(view, entity):
    from Benner.Tecnologia.Common import FieldDefinition

    if entity == None: return

    if entity.Fields["CONFIGAVALIACAO"].Handle.IsValid():
        view.FieldDefinitions["OPCAOAVALIACAO"].Required = True
    else:
        view.FieldDefinitions["OPCAOAVALIACAO"].Required = False
