def OnCLIENTEValueChanged(view, entity):
    from Benner.Tecnologia.Common import FieldDefinition

    if entity == None: return

    if entity.Fields["CLIENTE"].Handle.IsValid():
        view.FieldDefinitions["PROSPECT"].DataAccessLevel = FieldDefinition.AccessLevel.Read
        view.FieldDefinitions["CONTATOPROSPECT"].DataAccessLevel = FieldDefinition.AccessLevel.Read
    else:
        view.FieldDefinitions["PROSPECT"].DataAccessLevel = FieldDefinition.AccessLevel.Edit
        view.FieldDefinitions["CONTATOPROSPECT"].DataAccessLevel = FieldDefinition.AccessLevel.Edit

def OnPROSPECTValueChanged(view, entity):
    from Benner.Tecnologia.Common import FieldDefinition

    if entity == None: return

    if entity.Fields["PROSPECT"].Length > 0 :
        view.FieldDefinitions["CLIENTE"].DataAccessLevel = FieldDefinition.AccessLevel.Read
        view.FieldDefinitions["CONTATOCLIENTE"].DataAccessLevel = FieldDefinition.AccessLevel.Read
    else:
        view.FieldDefinitions["CLIENTE"].DataAccessLevel = FieldDefinition.AccessLevel.Edit
        view.FieldDefinitions["CONTATOCLIENTE"].DataAccessLevel = FieldDefinition.AccessLevel.Edit

def Load(widget):
    from Benner.Tecnologia.Common import FieldDefinition
    from Benner.Tecnologia.Common import Entity

    registro = widget.Entity
    if registro == None: return
    if (registro.Fields["AGENTEVENDAS"].Handle.IsValid()):
        widget.View.FieldDefinitions["AGENTEVENDAS"].DataAccessLevel = FieldDefinition.AccessLevel.Read
    else:
        widget.View.FieldDefinitions["AGENTEVENDAS"].DataAccessLevel = FieldDefinition.AccessLevel.Edit
