def Load(widget):
    if (widget.Entity != None):
        if widget.Entity.Fields["DESCONTO"] == None:
            widget.Entity.Fields["DESCONTO"] = "0"
        if widget.Entity.Fields["TAXA"] == None:
            widget.Entity.Fields["TAXA"] = "0"
