ScriptItemPortable.SourceCode:
def Load(widget):
    from Benner.Tecnologia.Common import EntityState
    form = widget.ProviderWidget
    entity = form.Entity
    entAux = widget.Entity
    if ( entity == None ):
        return
    entAux["TPBLOQUETOS"].SetNull()
    entAux["TPBLOQUETOS"].ReadOnly = False
    if (not entity["REEMITIRPARCSALDO"].GetBoolean()):
        entAux["TPBLOQUETOS"].ReadOnly = True
        if (entity["REEMITIRBLOQUETO"].GetBoolean()):
            entAux["TPBLOQUETOS"].SetInt32(2)
        else:
            entAux["TPBLOQUETOS"].SetInt32(3)
