ScriptItemPortable.SourceCode:
def OnOPERACAOValueChanged(view, entity):
    if entity == None: return

    elif entity.Fields["OPERACAO"].Value == 1:
        view.FieldDefinitions["DATAVENCIMENTO"].Visible = True
        view.FieldDefinitions["DOCSDATASDIFERENTES"].Visible = True
        view.FieldDefinitions["AGLUTINARDOCUMENTOS"].Visible = True
    elif entity.Fields["OPERACAO"].Value == 2:
        view.FieldDefinitions["DATAVENCIMENTO"].Visible = False
        view.FieldDefinitions["DOCSDATASDIFERENTES"].Visible = False
        view.FieldDefinitions["AGLUTINARDOCUMENTOS"].Visible = False
