def OnPADRAOFilter(args):
    from Benner.Tecnologia.Common import WhereClause
    from Benner.Tecnologia.Common import Parameter 
    args.WhereClause.Clear();

    if(not args.Entity["DATAINICIAL"].IsNull):
        args.WhereClause.AddWhereClause("A.DATAPROCESSAMENTO >= :DATAINICIAL", Parameter("DATAINICIAL", args.Entity["DATAINICIAL"].Value))
    if(not args.Entity["DATAFINAL"].IsNull):
        args.WhereClause.AddWhereClause("A.DATAPROCESSAMENTO <= :DATAFINAL", Parameter("DATAFINAL", args.Entity["DATAFINAL"].Value))
    if(not args.Entity["USUARIOS"].IsNull):
        args.WhereClause.AddWhereClause("A.USUARIO IN ( " + ", ".join(map(str, args.Entity["USUARIOS"].Value.ToHandleList())) + " )" )
    if(not args.Entity["FILIAIS"].IsNull):
        args.WhereClause.AddWhereClause("A.FILIAIS IN ( " + ", ".join(map(str, args.Entity["FILIAIS"].Value.ToHandleList())) + " )" )
