ScriptItemPortable.SourceCode:
def OnFILTROValueChanged(view, entity):
    from Benner.Tecnologia.Common import FieldDefinition;

    if (entity["FILTRO"].IsNull):
        entity["DOCUMENTODIGITADO"].Value = "";
        entity["DATAEMISSAOMAIOR"].Value = "";
        entity["DATAEMISSAOMENOR"].Value = "";
        entity.Fields["PESSOA"].Instance = None;
        view.FieldDefinitions["DATAEMISSAOMAIOR"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["DATAEMISSAOMENOR"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["DOCUMENTODIGITADO"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["PESSOA"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        return;

    if (int(entity.Fields["FILTRO"].Value) == 1) or (int(entity.Fields["FILTRO"].Value) == 3):
        entity["DOCUMENTODIGITADO"].Value = "";
        entity["DATAEMISSAOMAIOR"].Value = "";
        entity["DATAEMISSAOMENOR"].Value = "";
        view.FieldDefinitions["DATAEMISSAOMAIOR"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["DATAEMISSAOMENOR"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["DOCUMENTODIGITADO"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
        view.FieldDefinitions["PESSOA"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
        return;

    if (int(entity.Fields["FILTRO"].Value) == 2) or (int(entity.Fields["FILTRO"].Value) == 4):
        view.FieldDefinitions["DATAEMISSAOMAIOR"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
        view.FieldDefinitions["DATAEMISSAOMENOR"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
        view.FieldDefinitions["DOCUMENTODIGITADO"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
        view.FieldDefinitions["PESSOA"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
        return;

    if (int(entity.Fields["FILTRO"].Value) == 5):
        entity["DOCUMENTODIGITADO"].Value = "";
        entity["DATAEMISSAOMAIOR"].Value = "";
        entity["DATAEMISSAOMENOR"].Value = "";
        view.FieldDefinitions["DATAEMISSAOMAIOR"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["DATAEMISSAOMENOR"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["DOCUMENTODIGITADO"].DataAccessLevel = FieldDefinition.AccessLevel.Read;
        view.FieldDefinitions["PESSOA"].DataAccessLevel = FieldDefinition.AccessLevel.Edit;
