ScriptItemPortable.SourceCode:
def OcultarCampoClasse(visao):
	visao.FieldDefinitions["CLASSE"].Visible = False

def OcultarPeriodicidadesTipoWebEIntervalo(visao):
    periodicidade = visao.Pages["GERAL"].Items["PERIODICIDADE"]
    periodicidade.Pages["5"].Visible = False
    periodicidade.Pages["6"].Visible = False

def OcultarComandoNovo(visao):
    if visao.Commands["New"] != None:
		visao.Commands["New"].Visible = False

def Load(widget):
    OcultarPeriodicidadesTipoWebEIntervalo(widget.View)
    OcultarCampoClasse(widget.View)
