def OnSaveExecute(command, entity):
    if entity.TransitoryVars["valorBotoes"] == None:
        entity.Fields["BOTOES"] = 0
    else:
        entity.Fields["BOTOES"] = entity.TransitoryVars["valorBotoes"]

def Load(widget):
    from Benner.Tecnologia.Common import Entity

    entity = widget.Entity

    entity.TransitoryVars["valorBotoes"] = entity.Fields["BOTOES"];
