ScriptItemPortable.SourceCode:
def Load(widget):
    AjsutarVisibilidadeDicas(widget.View, widget.Entity)

def AjsutarVisibilidadeDicas(view, entity):
    tabValue = int(-1)
    if (entity <> None) :
        tabValue = entity.Fields['ANONIMIZAR'].Value
    if (view <> None) :
        view.FieldDefinitions['DICAVALORFIXO'].Visible = tabValue == 3
        view.FieldDefinitions['DICAEXPRESSAO'].Visible = tabValue == 4
