def OnCMD_CANCELARExecute(command, entity):
    from Benner.Tecnologia.Business.Tasks import BusinessTask

    BusinessTask.Factory.GetTask(entity.Handle).RequestAbort()

def InitializeRow(entity, row):
    if (entity["SITUACAO"].GetInt32() not in [1,6] ):
        row.GetCustomCommand("CMD_CANCELAR").Enabled = False;
